import os
import numpy as np

# Load experiment data
experiment_data = np.load(
    os.path.join(os.getcwd(), "working", "experiment_data.npy"), allow_pickle=True
).item()

# Extract metrics
for dataset_name, data in experiment_data.items():
    print(f"Dataset: {dataset_name}")
    for metric_name, values in data["metrics"].items():
        if "train" in metric_name:
            # Print the final training loss
            print(f"Final training loss: {values[-1]:.4f}")

